import React from 'react'
import {Form,Input,Select,Checkbox,DatePicker,Col,Radio,Button,Modal,message} from "antd"

const FormItem=Form.Item;
const Option=Select.Option;
const RadioGroup=Radio.Group;

class myForm extends React.Component{
    constructor(props){
        
        super(props);
        this.state={
            visible:false
        }
    }

    handleSubmit=(e)=>{
        e.preventDefault();
        console.log('收到表单值：',this.props.form.getFieldsValue())
        this.props.form.resetFields()
    }

    render() {
        const {getFieldProps}=this.props.form;
        const formItemLayout={
            labelCol:{span:3},
            warpperCol:{span:6}
        }

        const success=()=>{
            message.success('操作成功');
        }

        return (
            <Form horizontal onSubmit={this.handleSubmit}>
              <FormItem
                  id="control-input"
                  label="输入框"
                  {...formItemLayout}
                  required>
                  <Input id="control-input" placeholder="please enter..."
                      {...getFieldProps("userName")}/>
              </FormItem>

              <FormItem
                  label="日期选择框"
                  labelCol={{span:3}}
                  required>
                  <Col span="2">
                      <FormItem>
                          <DatePicker id="date1" {...getFieldProps("startDate")}/>
                      </FormItem>
                  </Col>
                  <Col span="1">
                      <p className="ant-form-split">--</p>
                  </Col>
                  <Col span="2">
                      <FormItem>
                          <DatePicker id="date2" {...getFieldProps("endDate")}/>
                      </FormItem>
                  </Col>
              </FormItem>

              <FormItem
                  id="contrl-textarea"
                  label="文本域"
                  {...formItemLayout}>
                  <Input type="textarea" id="control-textarea" rows="3"
                      {...getFieldProps('content')}/>
              </FormItem>

                <FormItem
                    id="select"
                    label="Select选择器"
                    {...formItemLayout}>
                    <Select
                        showSearch
                        style={{ width: 200 }}
                        placeholder="Select a person"
                        optionFilterProp="children"
                        filterOption={(input, option) =>
                            option.props.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                        }
                    >
                        <Option value="jack">Jack</Option>
                        <Option value="lucy">Lucy</Option>
                        <Option value="tom">Tom</Option>
                    </Select>
                    <Select id="select" size="large"
                          style={{width:150}} onChange={this.handleSelectChange}
                            {...getFieldProps('person')}>
                        <Option value="jack">jack</Option>
                        <Option value="lucy"></Option>
                    </Select>
                </FormItem>
            </Form>
        )
    }

}

export default myForm
