import React from 'react'
import {Form,Input,Select,Checkbox,DatePicker,Col,Radio,Button,Modal,message} from "antd"

const FormItem=Form.Item;
const Option=Select.Option;
const RadioGroup=Radio.Group;

class myForm extends React.Component{
    constructor(props){
        super(props);
        Form.create();
        this.state={
            visible:false
        }
    }

    handleSubmit=(e)=>{
        e.preventDefault();
        console.log('收到表单值：',this.props.form.getFieldsValue())
        this.props.form.resetFields()
    }

    render() {
        const { getFieldDecorator }=this.props.form;
        const formItemLayout={
            labelCol:{span:8},
            warpperCol:{span:16}
        }

        const success=()=>{
            message.success('操作成功');
        }

        return (
            <Form horizontal {...formItemLayout} onSubmit={this.handleSubmit}>
              <FormItem
                  id="control-input"
                  label="输入框" >
                  {getFieldDecorator('username',{
                      rules:[{
                          required:true,min:4,max :12,
                          message:'please input your name length 4-12'
                      },],
                  })(<Input type="text" id="control-input" placeholder="please enter name"/>)}

              </FormItem>

              <FormItem
                  id="contrl-textarea"
                  label="文本域">
                  {getFieldDecorator('content',{
                      rules: [{
                          required: true,min:50,message:'所填写数据不得少于50字符'
                      }]
                  })(<Input type="textarea" id="control-textarea" rows="3"/>)}

              </FormItem>

                <FormItem
                    id="select"
                    label="Select选择器"
                    {...formItemLayout}>
                    <Select
                        showSearch
                        style={{ width: 200 }}
                        placeholder="Select a person"
                        optionFilterProp="children"
                        filterOption={(input, option) =>
                            option.props.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                        }
                    >
                        <Option value="jack">Jack</Option>
                        <Option value="lucy">Lucy</Option>
                        <Option value="tom">Tom</Option>
                    </Select>
                </FormItem>
            </Form>
        )
    }

}

const WarppedmyForm=Form.create({name:'myform'})(myForm)

export default WarppedmyForm
