import React from 'react'
import {Table} from 'antd'
import {withRouter} from "react-router";

class myTable extends React.Component{
    constructor(props){
        super(props);
        this.state={
            tDate:[],
            selectedRowKeys:[]
        }
    }

    componentDidMount() {
        const showData=[]
         var datalist=fetch('../mokes/data.json', {
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                }
            })
                .then(res =>{
                    return res.json() })
             .then(data=>{
                 return data
             })
        //const  jsonobjdata=JSON.parse(data);

        // for(let i=0;i<jsonobjdata.length;i++){
        //     showData.push({
        //         key:i,
        //         name:jsonobjdata[i].title,
        //         age:0,
        //         address:"",
        //         remark:jsonobjdata[i].url,
        //         operate:"暂无"
        //     })
        // }
          this.setState({
              tDate:showData
          })
        }

     //checkbox状态
    onSelectChaneg=(selectedRowKeys)=>{
        this.setState({
            selectedRowKeys
        })
    }

    render() {
        const cloumns=[
            {
                title:"姓名",
                width:"20%",
                dataIndex:"name"
            },{
                title:"年龄",
                width:"20%",
                dataIndex:"age"
            },{
                title:"住址",
                width:"20%",
                dataIndex:"address"
            },{
                title:"备注",
                width:"20%",
                dataIndex:"remark",
                render(website){
                    return <a href={website} target="_blank">进入</a>
                }
            },{
                title:"操作",
                width:"20%",
                dataIndex:"operate"
            }
        ]

        const {selectedRowKeys} = this.state;
        const rowSelection = {
            selectedRowKeys,
            onchange:this.onSelectChaneg
        }
        const pagination={
            total:this.state.tDate.length,
            showSizeChanger:true,
            onShowSizeChange(current,pageSize){
                console.log('Current: ',current,'; PageSize: ',pageSize);
            },
            onchange(current){
                console.log('Current: ',current);
            }

        }
        return (
            <Table rowSelection={rowSelection} columns={cloumns} dataSource={this.state.tDate} bordered pagination={pagination}/>
        );
    }
}

export default withRouter(myTable)
