import React from 'react'
import {Table} from 'antd'
import {withRouter} from "react-router";

class myTable extends React.Component{
    constructor(props){
        super(props);
        this.state={
            tData:[],
            selectedRowKeys:[]
        }
    }


    componentDidMount() {
        const showData=[]
        fetch('../mokes/data.json', {
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json'
            }
        })
            .then(res =>{
                return res.json() })
            .then(data=>{
                console.log("datalength:",data.length)
                for(let i=0;i<data.length;i++){
                    showData.push({
                        key:i,
                        name:data[i].title,
                        age:0,
                        address:'',
                        remark:data[i].url,
                        operate:"暂无"
                    })
                }
                console.log("showdata:",showData);
            })

        this.setState({
            tData:showData
        })

        const data = []

        for (let i = 0; i < 46; i++) {
            data.push({
                key: i,
                name: `Mr劳卜${i}`,
                age: 18,
                address: `西湖区湖底公园${i}号`,
                remark: 'http://www.cnblogs.com/luozhihao/',
                operate: '暂无'
            })
        }
        console.log(data)
        this.setState({
            tData: data
        })
        }

    componentWillUnmount() {
        console.log("delete:",this.state.tData)
    }

    //checkbox状态
    onSelectChange=(selectedRowKeys)=>{
        this.setState({
            selectedRowKeys
        })
    }

    render() {
        const clomp=[{
                title:'姓名',
                width:'20%',
                dataIndex:'name',
            },{
                title:'年龄',
                width:'20%',
                dataIndex:'age',
            },{
                title:'住址',
                width:'20%',
                dataIndex:'address',
            },{
                title:'备注',
                width:'20%',
                dataIndex:'remark',
                render(website){
                    return <a href={website} target="_blank" rel="noopener noreferrer">进入</a>
                }
            },{
                title:'操作',
                width:'20%',
                dataIndex:'operate',
            }]

        const {selectedRowKeys} = this.state;
        const rowSelection = {
            selectedRowKeys,
            onchange:this.onSelectChange
        }
        const pagination={
            total:this.state.tData.length,
            showSizeChanger:true,
            onShowSizeChange(current,pageSize){
                console.log('Current: ',current,'; PageSize: ',pageSize);
            },
            onchange(current){
                console.log('Current: ',current);
            }

        }
        return (
            <Table rowSelection={rowSelection} columns={clomp} dataSource={this.state.tData} bordered pagination={pagination}/>
        );
    }
}

export default withRouter(myTable)
